import React, { useState } from 'react'; // Removed useEffect as it's not needed for dynamic background
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

// Register Chart.js components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

// IMPORTANT: Replace 'YOUR_API_KEY_HERE' with your actual OpenWeatherMap API key
const API_KEY = "d34d1cbe248782556ada85ba143954ca"; // <<< REPLACE THIS WITH YOUR API KEY <<<

function App() {
  const [city, setCity] = useState('');
  const [weatherData, setWeatherData] = useState(null);
  const [forecastData, setForecastData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const getWeather = async () => {
    setLoading(true);
    setError(null);
    setWeatherData(null); // Clear previous weather data
    setForecastData(null); // Clear previous forecast data

    if (!city) {
      setError('Please enter a city name.');
      setLoading(false);
      return;
    }

    const weatherUrl = `https://api.openweathermap.org/data/2.5/weather?q=${encodeURIComponent(city)}&appid=${API_KEY}&units=metric`;
    const forecastUrl = `https://api.openweathermap.org/data/2.5/forecast?q=${encodeURIComponent(city)}&appid=${API_KEY}&units=metric`;

    try {
      const [weatherRes, forecastRes] = await Promise.all([
        fetch(weatherUrl),
        fetch(forecastUrl)
      ]);

      if (!weatherRes.ok || !forecastRes.ok) {
        const errorData = await weatherRes.json();
        throw new Error(errorData.message || "City not found or API error");
      }

      const currentWeatherData = await weatherRes.json();
      const forecastWeatherData = await forecastRes.json();

      setWeatherData(currentWeatherData);

      // Prepare chart data (next 8 intervals = ~24h)
      const labels = forecastWeatherData.list.slice(0, 8).map(entry =>
        new Date(entry.dt_txt).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })
      );
      const temps = forecastWeatherData.list.slice(0, 8).map(entry => entry.main.temp);
      setForecastData({ labels, temps });

    } catch (err) {
      console.error("Error fetching weather data:", err);
      setError(`Error fetching weather data. ${err.message}`);
    } finally {
      setLoading(false);
    }
  };

  const chartDataConfig = {
    labels: forecastData?.labels || [],
    datasets: [{
      label: 'Temperature (°C)',
      data: forecastData?.temps || [],
      borderColor: 'rgba(255, 255, 255, 0.9)',
      backgroundColor: 'rgba(255, 255, 255, 0.2)',
      fill: true,
      tension: 0.3,
      pointRadius: 4,
      pointHoverRadius: 6
    }]
  };

  const chartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    scales: {
      x: {
        ticks: { color: '#fff' },
        grid: { color: 'rgba(255,255,255,0.1)', drawBorder: false }
      },
      y: {
        beginAtZero: false,
        ticks: { color: '#fff' },
        grid: { color: 'rgba(255,255,255,0.1)', drawBorder: false }
      }
    },
    plugins: {
      legend: { labels: { color: '#fff' } },
      tooltip: {
        backgroundColor: 'rgba(0,0,0,0.7)',
        titleColor: '#fff',
        bodyColor: '#fff',
        borderColor: '#fff',
        borderWidth: 1
      }
    }
  };

  return (
    <div className="container">
      <h1>Weather Forecast</h1>
      <input
        type="text"
        id="cityInput"
        placeholder="Enter City Name"
        value={city}
        onChange={(e) => setCity(e.target.value)}
        onKeyPress={(e) => {
          if (e.key === 'Enter') {
            getWeather();
          }
        }}
      />
      <button onClick={getWeather} disabled={loading}>
        {loading ? 'Loading...' : 'Get Weather'}
      </button>

      {error && (
        <div id="weatherDisplay" style={{ display: 'block' }}>
          <p className="error">{error}</p>
        </div>
      )}

      {weatherData && !loading && !error && (
        <div id="weatherDisplay" style={{ display: 'block' }}>
          <h2>{weatherData.name}, {weatherData.sys.country}</h2>
          <p><strong>Temperature:</strong> {weatherData.main.temp.toFixed(1)}°C</p>
          <p><strong>Condition:</strong> {weatherData.weather[0].description
            .split(' ')
            .map(word => word.charAt(0).toUpperCase() + word.slice(1))
            .join(' ')}</p>
          <p><strong>Humidity:</strong> {weatherData.main.humidity}%</p>
          <p><strong>Wind:</strong> {weatherData.wind.speed.toFixed(1)} m/s</p>
        </div>
      )}

      {forecastData && !loading && !error && (
        <div id="forecastChart" style={{ display: 'block' }}>
          <Line data={chartDataConfig} options={chartOptions} />
        </div>
      )}
    </div>
  );
}

export default App;
