import React, { useState } from 'react';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  Filler, // <-- ADDED Filler for background color
} from 'chart.js';

// Register Chart.js components - This is correctly placed
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  Filler // <-- REGISTER Filler
);

// IMPORTANT: Replace 'YOUR_API_KEY_HERE' with your actual OpenWeatherMap API key
const API_KEY = "d34d1cbe248782556ada85ba143954ca"; // <<< REPLACE THIS WITH YOUR API KEY <<<

function App() {
  const [city, setCity] = useState('');
  const [weatherData, setWeatherData] = useState(null);
  const [forecastChartData, setForecastChartData] = useState({ labels: [], datasets: [] }); // Initialize with empty structure
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const getWeather = async () => {
    setLoading(true);
    setError(null);
    setWeatherData(null);
    setForecastChartData({ labels: [], datasets: [] }); // Reset chart data

    if (!city) {
      setError('Please enter a city name.');
      setLoading(false);
      return;
    }

    const weatherUrl = `https://api.openweathermap.org/data/2.5/weather?q=${encodeURIComponent(city)}&appid=${API_KEY}&units=metric`;
    const forecastUrl = `https://api.openweathermap.org/data/2.5/forecast?q=${encodeURIComponent(city)}&appid=${API_KEY}&units=metric`;

    try {
      const [weatherRes, forecastRes] = await Promise.all([
        fetch(weatherUrl),
        fetch(forecastUrl)
      ]);

      if (!weatherRes.ok || !forecastRes.ok) {
        const errorData = await weatherRes.json();
        throw new Error(errorData.message || "City not found or API error");
      }

      const currentWeatherData = await weatherRes.json();
      const forecastWeatherData = await forecastRes.json();

      setWeatherData(currentWeatherData);

      const labels = forecastWeatherData.list.slice(0, 8).map(entry =>
        new Date(entry.dt_txt).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })
      );
      const temps = forecastWeatherData.list.slice(0, 8).map(entry => entry.main.temp);
      
      setForecastChartData({
        labels: labels,
        datasets: [{
          label: 'Temperature (°C)',
          data: temps,
          borderColor: 'rgba(123, 220, 181, 0.9)', // Brighter color like the button
          backgroundColor: 'rgba(123, 220, 181, 0.2)', // Lighter fill
          fill: true, // Ensure fill is enabled
          tension: 0.3,
          pointRadius: 4,
          pointBackgroundColor: 'rgba(123, 220, 181, 1)', // Make points solid
          pointBorderColor: '#fff',
          pointHoverRadius: 6,
          pointHoverBackgroundColor: '#fff',
          pointHoverBorderColor: 'rgba(123, 220, 181, 1)',
        }]
      });

    } catch (err) {
      console.error("Error fetching weather data:", err);
      setError(`Error fetching weather data. ${err.message}`);
    } finally {
      setLoading(false);
    }
  };

  const chartOptions = {
    responsive: true,
    maintainAspectRatio: false, // This is good
    scales: {
      x: {
        ticks: { color: '#e0e0e0' }, // Lighter color for ticks
        grid: { color: 'rgba(255,255,255,0.1)', drawBorder: false }
      },
      y: {
        beginAtZero: false,
        ticks: { color: '#e0e0e0', stepSize: 5 }, // Lighter color & suggest step size
        grid: { color: 'rgba(255,255,255,0.1)', drawBorder: false }
      }
    },
    plugins: {
      legend: {
        labels: { color: '#e0e0e0' } // Lighter color
      },
      tooltip: {
        backgroundColor: 'rgba(0,0,0,0.8)', // Slightly more opaque
        titleColor: '#fff',
        bodyColor: '#fff',
        borderColor: 'rgba(123, 220, 181, 0.7)',
        borderWidth: 1
      }
    }
  };

  // Determine if chart should be visible
  const showChart = forecastChartData.labels && forecastChartData.labels.length > 0 && !loading && !error;

  return (
    <div className="container">
      <h1>Weather Forecast</h1>
      <input
        type="text"
        id="cityInput"
        placeholder="Enter City Name"
        value={city}
        onChange={(e) => setCity(e.target.value)}
        onKeyPress={(e) => { if (e.key === 'Enter') getWeather(); }}
      />
      <button onClick={getWeather} disabled={loading}>
        {loading ? 'Loading...' : 'Get Weather'}
      </button>

      {error && (<div id="weatherDisplay" style={{ display: 'block' }}><p className="error">{error}</p></div>)}
      
      {weatherData && !loading && !error && (
        <div id="weatherDisplay" style={{ display: 'block' }}>
          <h2>{weatherData.name}, {weatherData.sys.country}</h2>
          <p><strong>Temperature:</strong> {weatherData.main.temp.toFixed(1)}°C</p>
          <p><strong>Condition:</strong> {weatherData.weather[0].description.split(' ').map(word => word.charAt(0).toUpperCase() + word.slice(1)).join(' ')}</p>
          <p><strong>Humidity:</strong> {weatherData.main.humidity}%</p>
          <p><strong>Wind:</strong> {weatherData.wind.speed.toFixed(1)} m/s</p>
        </div>
      )}
      
      {showChart && ( // Conditionally render the chart container
        <div id="forecastChart" style={{ display: 'block', height: '250px', width: '100%' }}> {/* Added height */}
          <Line data={forecastChartData} options={chartOptions} />
        </div>
      )}
    </div>
  );
}

export default App;
